import styles from "styles/views/question.create.module.scss";
import {Button, Card, Col, Divider, Form, Image, Input, message, Row} from "antd";
import React, {useEffect, useState} from "react";
import {getTopComments, insertComment} from "helpers/api/comment";
import {getAnswer} from "helpers/api/answer";
import axios from "axios";
import useAuth from "helpers/api/auth";
import { useHistory, useParams } from "react-router-dom";
const requests = axios.create({
  baseURL: process.env.API_HOST, // Change to your desired host and port
});


const AnswerComments = (  ) => {
    useAuth();
    const { id } = useParams();
    const router = useHistory();
    // const { id } = router.query
    const [form] = Form.useForm();
    const [answer, setAnswer] = useState();
    const [comments, setComments] = useState([]);
    // console.log(id)
    useEffect(() => {
        const fetchData = async () => {
            console.log("response1.data");
            try {
                const [response1, response2] = await Promise.all([
                    requests.get("/answer/getAnswerById", {
                        params: {
                            ID: id,
                        },
                    }),
                    // requests.get("/comment/getTopComments", {
                    //     params: {
                    //         to_comment: id,
                    //         to_comment_type: 0,
                    //     },
                    // }),
                ]);

                setAnswer(response1.data);
                setComments(response2.data?.comments);
            } catch (error) {
                // 处理错误
                console.error("Error fetching answer and comments:", error);
            }
        };

        fetchData();
    }, [id]);

    const handleReply = (username) => {
        form.setFieldsValue({
            content: `Reply to ${username}: `
        });
    };

    const handleClick = (values) => {

        insertComment({
            to_comment: id,
            to_comment_type: 0,
            content: values.content
        }).then((response) => {
            if (response.success === 'true') {
                message.info('回复成功')
                form.setFieldsValue({
                    content: ''
                })
                getTopComments({
                    to_comment: id,
                    to_comment_type: 0
                }).then(response => {
                    if (response.success === 'true') {
                        setComments(response.comments)
                    }
                })
            } else {
                message.error('回复失败')
            }
        });
    };
    const handleBack = () => {
        router.goBack();
    };
    // useEffect()
    // console.log(answer)
    return (
        <div className={styles.container}>
            <div className={styles.main}>
                { answer &&  <Card
                    style={{ width: 765 }}
                    cover={<img style={{ height: '256px', objectFit: 'cover', objectPosition: 'center' }} alt="example" src="https://cdn.pixabay.com/photo/2020/04/19/08/17/watercolor-5062356_960_720.jpg" />}
                >
                    <Row>
                        <Col span={3}>
                            <Image preview={false} width={48} height={48} src={'error'} fallback="data:image/png;base64,"/>
                            <p className={styles.name} style={{ fontSize: '12px', marginTop: '4px' }}>{ answer.username }</p>
                        </Col>

                        <Col span={18}>
                            <p style={{ marginTop: '0px' }}>{ answer.answer.content }</p>
                        </Col>

                        <Col>
                            <Button onClick={handleBack}>Back</Button>
                        </Col>
                    </Row>

                    <div>
                        <p style={{ fontWeight: 600, fontSize: '16px' }}>{ comments.length } comments</p>
                        <Divider/>

                        { comments.map((comment) => {
                            return <div key={comment.comment.id}>
                                <Row>
                                    <Col span={3}>
                                        <Image preview={false} width={48} height={48} src={comment.commentator.headPhotoUrl} fallback="data:image/png;base64,"/>
                                        <p className={styles.name} style={{ fontSize: '12px', marginTop: '4px' }}>{comment.commentator.username}</p>
                                    </Col>

                                    <Col span={18}>
                                        <p style={{ marginTop: '0px' }}>{comment.comment.content}</p>
                                    </Col>

                                    <Col>
                                        <Button onClick={() => handleReply(comment.commentator.nickname)} style={{ backgroundColor: '#6F3BF5' }} type={"primary"}>Reply</Button>
                                    </Col>
                                </Row>

                                <Divider/>
                            </div>
                        }) }

                    </div>

                    <div>
                        <Form form={form} onFinish={handleClick}>
                            <Form.Item name={'content'} rules={[{ required: true, message: "please input your comment." }]}>
                                <Input.TextArea placeholder={"Type your comment here:"} size={"large"}></Input.TextArea>
                            </Form.Item>

                            <Form.Item>
                                <Button htmlType={"submit"} type={"primary"} style={{ backgroundColor: '#6F3BF5', float: 'right', marginTop: '16px' }}>Send</Button>
                            </Form.Item>
                        </Form>
                    </div>
                </Card>}
            </div>
        </div>
    )

}



export default AnswerComments